import React from 'react';
import classes from './SecondPost.module.css'
import {NavLink} from "react-router-dom";
import MessageSend from "./Message/MessageSend";
import DialigItem from "./Dialogitem/DialigItem";
import {sendMessageCreator, updateNewMessageBodyCreator} from "../../redux/state";

const SecondPost = (props) => {

    let messagesElements = props.messagesData
        .map(message => <MessageSend message={state.messagesData.message} />);


    let state = props.store.getState();



    let newMessageBody = props.state.newMessageBody;
    let onSendMessageClick = () => {
        props.store.dispatch(sendMessageCreator());
    }
    let onNewMessageChange = (e) => {
       let body = e.target.value;
        props.store.dispatch(updateNewMessageBodyCreator(body));
    }

    return (
        <div className={classes.dialogs}>

            <div className={classes.dialogsItem}>
                <DialigItem name={state.dialogsData[0].name} id={state.dialogsData[0].id}/>
                <DialigItem name={state.dialogsData[1].name} id={state.dialogsData[1].id}/>
                <DialigItem name={state.dialogsData[2].name} id={state.dialogsData[2].id}/>
            </div>
            <div className={classes.messages}>
                <div>
                <MessageSend message={state.messagesData[0].message} />
                <MessageSend message={state.messagesData[1].message}/>
                <MessageSend message='ho'/>
                </div>
                <div><textarea value={newMessageBody}
                               onChange={onNewMessageChange}
                               placeholder=''>enter your message </textarea></div>
                <div><button onClick={ onSendMessageClick}>Send</button></div>
            </div>
            <div className={classes.dot}>
                <div>-</div>
                <div>-</div>
                <div>-</div>
            </div>
        </div>
    );
};

export default SecondPost;
