import React from 'react';

//клаsсовая компонента

class ProfileStatus extends React.Component {
    state = {
        editMode: false // режим редактирования
    }

    activateEditMode = () => {
        this.setState({   // тип метод для перерисовки , лучше почитать / setState - асинхронин
            editMode: true
        })
}
    deactivateEditMode = () => {
        this.setState({
            editMode: false
        });
        this.props.updateStatus(this.statusInputRef.current.value);
    }

    render() {
        return (
            <div>
                {!this.state.editMode &&
                    <div>
                        <span onDoubleClick={this.activateEditMode }>{this.props.status}</span>
                    </div>
                }
                {this.state.editMode &&
                    <div>
                        <input ref={this.statusInputRef} autoFocus={true} onBlur={this.deactivateEditMode}
                               value={this.state.status}/>
                    </div>
                }
            </div>
        );
    };
}

export default ProfileStatus;
