import React from 'react';
import PostCar from "./PostCar/PostCar";

const Car = (props) => {
//df
    let postElements3 =
        props.messagesCar.map(p => <PostCar message={p.message}/>);

    let newPostElement = React.createRef();

    let addPost = () => {
        props.addPost();
    }
    let onPostChange = () => {
        let newCar = newPostElement.current.value;
        props.updateNewPostTextCarCreator(newCar);
    }
    return (
        <div>
            <form>
                <div>
                <textarea onChange={onPostChange}
                          ref={newPostElement}
                          value={props.newPostCar}/>
                </div>
                <div>
                    <button onClick={addPost}>Add post</button>
                </div>
            </form>
            <div>
                {postElements3}
            </div>
        </div>
    );
};

export default Car;
