const SEND_CAR = 'SEND_CAR';
const UPDATE_NEW_MESSAGE_CAR = 'UPDATE_NEW_MESSAGE_CAR';

let initialState = {
    messagesCar: [
        {id: 1, message: 'BMW'},
    ],
    newPostCar: ' '
};
const CarReducer = (state = initialState, action) => {
    /*     switch (action.type) {
             case SEND_CAR: {
                 let newMessagesCar = {
                     id: 2,
                     message: state.newPostCar,
                 };
                 let stateCopyCar = {...state};
                 stateCopyCar.messagesCar = [...state.messagesCar];
                 stateCopyCar.messagesCar.push(newMessagesCar);
                 stateCopyCar.newPostCar = ''
                 return stateCopyCar;
             }
             case UPDATE_NEW_MESSAGE_CAR: {
                 let stateCopyCar = {...state};
                 stateCopyCar.newPostCar = action.newCar;
                 return stateCopyCar;
             }
         }
         return state;
    }*/

    switch (action.type) {
        case SEND_CAR:
            return {
                ...state,
                newPostCar: '',
                MessagesCar: [...state.messagesCar, {id: 2, message: state.newPostCar}]
            }
        case UPDATE_NEW_MESSAGE_CAR:
            return {
                ...state,
                newPostCar: action.newCar,
            }
    }
    return state;
}


export const addPostCarCreator = () => {
    return {
        type: SEND_CAR
    }
}
export const updateNewPostTextCarCreator = (newCar) => {
    return {type: UPDATE_NEW_MESSAGE_CAR, newCar: newCar}
}
export default CarReducer;
