const FOLLOW = 'FOLLOW';
const UNFOLLOW = 'UNFOLLOW';
const SET_USERS = 'SET_USERS';


let initialState = {
    users: [
        {id: 1, photoUrl:'https://upload.wikimedia.org/wikipedia/commons/thumb/6/6a/Wien_-_Flakturm_Augarten_%281%29.JPG/640px-Wien_-_Flakturm_Augarten_%281%29.JPG',
            followed: false, fullName: 'Dima', status: 'boss', location: {city: 'Kiev', country: 'Ukraine'}},
        {id: 2, photoUrl:'https://upload.wikimedia.org/wikipedia/commons/thumb/6/6a/Wien_-_Flakturm_Augarten_%281%29.JPG/640px-Wien_-_Flakturm_Augarten_%281%29.JPG',
             followed: true, fullName: 'Dima2', status: 'boss2', location: {city: 'Kiev2', country: 'Ukraine'}},
        {id: 3, photoUrl:'https://upload.wikimedia.org/wikipedia/commons/thumb/6/6a/Wien_-_Flakturm_Augarten_%281%29.JPG/640px-Wien_-_Flakturm_Augarten_%281%29.JPG',
            followed: false, fullName: 'Dima3', status: 'boss3', location: {city: 'Kiev3', country: 'Ukraine'}},
    ]
};

const usersReducer = (state = initialState, action) => {

    switch (action.type) {
        case FOLLOW:
            return {
                ...state,                             // создаем копию стейта
                users: state.users.map(u => {         // делам копию users и спомощью меп создаем по одному элементу и
                    if (u.id === action.userID) {     // заходим внутирь , если id совпадает с id из екшин то меняем followed
                        return {...u, followed: true} // и добавляем измененный(скопированній) элемент в наш   массив,
                    }                                 // если id не совпадает то возращаем элемент без изменений
                    return u;
                })
            }

        case UNFOLLOW:
            return {
                ...state,
                users: state.users.map ( u => {
                    if (u.id === action.userID){
                        return { ...u, followed: false}
                    }
                    return u;
                })
            }

        case SET_USERS:{
            return { ...state, users: [...state.users, ...action.users]}
        }
    }
    return state;
}

export const followAC = (userID) => ({type: FOLLOW, userID})
export const unfollowAC = (userID) => ({type: UNFOLLOW, userID})
export const setUsersAC = (users) => ({type: SET_USERS, users})

export default usersReducer;
