import React from 'react';
import ProfileContainer from "./ProfileContainer";
import {connect} from "react-redux";
import ProfileObert from "./ProfileObert";
import axios from "axios";


class ProfileBossContainer extends  React.Component{
    componentDidMount() {
        axios.get(`https://social-network.samuraijs.com/api/1.0/profile?page=${this.props.currentPage}&count=${this.props.pageSize}`)
            .then(response => {
                this.props.toggleIsFetching(false);
                this.props.setUsers(response.data.items);
                this.props.setTotalUsersCount(response.data.totalCount);
            });
    }

    render() {
        return (
            <div>
                <ProfileObert {...this.props} />
            </div>
        );
    };
}
export default ProfileBossContainer;
