import profileReducer from "./profile-reducer";
import CarReducer from "./car-reducer";

let store = {
    _state: {
        dialogsData: [
            {id: 1, name: 'Dima'},
            {id: 2, name: 'Andre'},
            {id: 3, name: 'Sasha'},
        ],
        messagesData: [
            {id: 1, message: 'hi'},
            {id: 2, message: 'hi'},
            {id: 3, message: 'hi'},
            {id: 4, message: 'hi'},
        ],
        posts: [
            {id: 1, message: 'hi'},
            {id: 2, message: 'ho'}
        ],
        messagesCar: [
            {id: 1, message: 'BMW'},
        ],
        newPostText: ' re',
        newMessageBody: ' ',
        newPostCar: ' ',

    },
    _callSubscriber() {
        console.log('state is change');
    },
    getState() {
        return this._state;
    },
    subscribe(observer) {
        this._callSubscriber = observer;
    },

    dispatch(action) {

        this._state = profileReducer(this._state, action);
        this._state = CarReducer(this._state, action);

        this._callSubscriber(this._state);

    }
}



export const addPostCarCreator = () => {
    return {
        type: SEND_CAR
    }
}
export const updateNewPostTextCarCreator = (newCar) => {
    return {type: UPDATE_NEW_MESSAGE_CAR, newCar: newCar}
}


export default store;
window.state = store;
