import {userAPI} from "../api/api";

const ADD_POST = 'ADD-POST';
const UPDATE_NEW_POST_TEXT = 'UPDATE-NEW-POST-TEXT';
const SET_USER_PROFILE = 'SET_USER_PROFILE';
const SET_STATUS = 'SET_STATUS';

let initialState = {
    posts: [
        {id: 1, message: 'hi'},
        {id: 2, message: 'ho'}
    ],
    newPostText: ' re',
    profile: null,
    status: '',
};

const ProfileReducer = (state = initialState, action) => {

    switch (action.type) {
        case ADD_POST:
            return {
                ...state,// создоем stateCopy и копируем в него  state (поверхносно)
                newPostText: '',//затираем
                posts: [...state.posts, {id: 3, message: state.newPostText}]//создоем новый массив и забираем элементы с помощю ... из старого  массиваи добавляем нью пост
            }
        case UPDATE_NEW_POST_TEXT:
            return {
                ...state,
                newPostText: action.newText,
            }
        case SET_USER_PROFILE:
            return { ...state, profile:action.profile }
    }
    return state;
}

export const addPostActionCreator = () => {
    return {
        type: ADD_POST
    }
}
export const setUsersProfile = (profile) => {
    return {
        type: SET_USER_PROFILE, profile
    }
}

export const getUsersProfile = (userId) => (diapatch) => {
    userAPI.getProfile(userId).then(response => {
        diapatch(setUsersProfile(response.data));
    });
}
export const updateNewPostTextActionCreator = (text) => {
    return {type: UPDATE_NEW_POST_TEXT, newText: text}
}
export default ProfileReducer;
