let rerender = () => {
    c
}

let state = {
    dialogsData: [
        {id: 1, name: 'Dima'},
        {id: 2, name: 'Andre'},
        {id: 3, name: 'Sasha'},
    ],
    messagesData: [
        {id: 1, message: 'hi'},
        {id: 2, message: 'hi'},
        {id: 3, message: 'hi'},
        {id: 4, message: 'hi'},
    ],
    posts: [
        {id: 1, message: 'hi'},
        {id: 2, message: 'ho'}
    ],
    newPostText: ' re'
}
window.state = state;

export let addPost = () => {
    let newPost = {
        id: 3,
        message: state.newPostText,
        /*likesCount:0*/
    };

    state.posts.push(newPost);
    state.newPostText = ''
    rerender(state);
}

export let updateNewPostText = (newText) => {
    state.newPostText = newText;
    rerender(state);
}
export default state;
