import React from 'react';
import Post from "./Post/Post";
import Infinity from "../Common/Infinity/Infinity";
import ProfileStatus from "./ProfileStatus/ProfileStatus";

const Profile = (props) => {
    if(!props.profile){
        return <Infinity/>
    }
    let postElements2 =
        props.posts.map(p => <Post message={p.message} /*key = {p.id}*//>);

    let newPostElement = React.createRef();

    let onAddPost = () => {
        props.addPost();
    }
    let onPostChange = () => {
        let text = newPostElement.current.value;
        props.updateNewPostTextActionCreator(text);
    }
    return (
        <div>
            {/*<div>
                <img
                    src='https://www.bmw.by/content/dam/bmw/common/all-models/4-series/gran-coupe/2021/navigation/bmw-4-series-gran-coupe-modelfinder-890x501.png'/>
            </div>*/}
            <div>
                <img src={props.profile.photos.large} />
                <ProfileStatus status={"hello"}/>
            </div>
            <div>
                <p>df</p>
                <p>df</p>
                <p>df</p>
                <p>df</p>
            </div>
            <div>
                <textarea onChange={onPostChange} ref={newPostElement}
                          value={props.newPostText}/>
            </div>
            <div>
                <button onClick={onAddPost}>Add post</button>
            </div>
            <div>
                {postElements2}
            </div>
            {/*<Post/>*/}
        </div>
    );
};
export default Profile;
