import React from 'react';
import styles from './Users.module.css'

const Users = (props) => {

    props.setUsers([
            {
                id: 1,
                photoUrl: 'https://upload.wikimedia.org/wikipedia/commons/thumb/6/6a/Wien_-_Flakturm_Augarten_%281%29.JPG/640px-Wien_-_Flakturm_Augarten_%281%29.JPG',
                followed: false,
                fullName: 'Dima',
                status: 'boss',
                location: {city: 'Kiev', country: 'Ukraine'}
            },
            {
                id: 2,
                photoUrl: 'https://upload.wikimedia.org/wikipedia/commons/thumb/6/6a/Wien_-_Flakturm_Augarten_%281%29.JPG/640px-Wien_-_Flakturm_Augarten_%281%29.JPG',
                followed: true,
                fullName: 'Dima2',
                status: 'boss2',
                location: {city: 'Kiev2', country: 'Ukraine'}
            },
            {
                id: 3,
                photoUrl: 'https://upload.wikimedia.org/wikipedia/commons/thumb/6/6a/Wien_-_Flakturm_Augarten_%281%29.JPG/640px-Wien_-_Flakturm_Augarten_%281%29.JPG',
                followed: false,
                fullName: 'Dima3',
                status: 'boss3',
                location: {city: 'Kiev3', country: 'Ukraine'}
            },
        ]
    )
    return (
        <div>
            {
                props.users.map(u => <div key={u.id}>
                    <span>
                        <div>
                            <img src={u.photoUrl} className={styles.userPhoto}/>
                        </div>
                        <div>
                            {u.followed
                                ? <button onClick={() => {
                                    props.unfollow(u.id)
                                }}> Unfollow</button>
                                : <button onClick={() => {
                                    props.follow(u.id)
                                }}> Follow</button>}
                        </div>
                    </span>
                    <span>
                        <span>
                            <div>{u.fullName}</div>
                            <div>{u.status}</div>
                        </span>
                            <span>
                                <div>{u.location.country}</div>
                                <div>{u.location.city}</div>
                            </span>
                    </span>
                </div>)
            }
        </div>
    )
}

export default Users;
