const SEND_CAR = 'SEND_CAR';

let initialState = {
    messagesCar: [
        {id: 1, message: 'BMW'},
    ],
};
const CarReducer = (state = initialState, action) => {
    /*     switch (action.type) {
             case SEND_CAR: {
                 let newMessagesCar = {
                     id: 2,
                     message: state.newPostCar,
                 };
                 let stateCopyCar = {...state};
                 stateCopyCar.messagesCar = [...state.messagesCar];
                 stateCopyCar.messagesCar.push(newMessagesCar);
                 stateCopyCar.newPostCar = ''
                 return stateCopyCar;
             }
             case UPDATE_NEW_MESSAGE_CAR: {
                 let stateCopyCar = {...state};
                 stateCopyCar.newPostCar = action.newCar;
                 return stateCopyCar;
             }
         }
         return state;
    }*/

    switch (action.type) {
        case SEND_CAR:
            return {
                ...state,
                newPostCar: '',
                messagesCar: [...state.messagesCar, {id: 2, message: action.newPostCar}]
            }
    }
    return state;
}


export const addPostCarCreator = (newPostCar) => {
    return {
        type: SEND_CAR, newPostCar
    }
}

export default CarReducer;
