import React from 'react';
import ProfileContainer from "./ProfileContainer";
import {connect} from "react-redux";
import {getUsersProfile} from "../../redux/profile-reducer";
import {withRouter} from "react-router-dom";
import Redirect from "react-router-dom/es/Redirect";
import {withAuthRedirect} from "../../hoc/withAuthRedirect";

//df
class ProfileBossContainer extends  React.Component{
    componentDidMount() {
        let userId =this.props.match.params.userId; // метод позволяющий получить userId использ withRouter
        if(!userId){
            userId = 2;
        }
        this.props.getUsersProfile(userId);
    }

    render() {
        return (
            <div>
                <ProfileContainer {...this.props} profile={this.props.profile}/>
            </div>
        );
    };
}

let AuthRedirectComponent = withAuthRedirect(ProfileBossContainer);
(props) => {
    if (!this.props.isAuth /*this.props.isAuth === false*/) return <Redirect to="/login" />;
    return <ProfileBossContainer {...props}/>
}

let mapStateToProps = (state) => ({
 profile: state.profilePage.profile,
    isAuth: state.auth.isAuth,
});

let WithUrlDataContainerComponent = withRouter(AuthRedirectComponent);

export default connect(mapStateToProps, {getUsersProfile}) (WithUrlDataContainerComponent);
