import {rerender} from '../render';

let state = {
    dialogsData: [
        {id: 1, name: 'Dima'},
        {id: 2, name: 'Andre'},
        {id: 3, name: 'Sasha'},
    ],
    messagesData: [
        {id: 1, message: 'hi'},
        {id: 2, message: 'hi'},
        {id: 3, message: 'hi'},
        {id: 4, message: 'hi'},
    ],
    posts: [
        {id: 1, message: 'hi'},
        {id: 2, message: 'ho'}
    ],
    newPostText: 'it'
}

export let addPost = (postMessage) => {
    let newPost = {
        id: 3,
        message: postMessage,
        /*likesCount:0*/
    };

    state.posts.push(newPost);
    rerender(state);
}

export let updateNewPostText = (newText) => {
    state.posts.push(newPostText);
    rerender(state);
}
export default state;
