import React from 'react';
import Post from "../Profile/Post/Post";
import MessageSend from "../SecondPost/Message/MessageSend";
import PostCar from "./PostCar/PostCar";

let addPostCarCreator = () => {
    return {
        type: 'SEND_CAR'
    }
}
let updateNewPostTextCarCreator = (newCar) => {
    return {
        type: 'UPDATE_NEW_MESSAGE_CAR', newCar: newCar
    }
}




const Car = (props) => {

    let postElements3 =
        props.messagesCar.map(p => <PostCar message={p.message}/>);



    let newPostElement = React.createRef();

    let addPost = () => {
        props.dispatch(addPostCarCreator());
    }

    let onPostChange = () => {
        let newCar = newPostElement.current.value;
        let action = updateNewPostTextCarCreator(newCar);
        props.dispatch(action);
    }

    return (
        <div>
            <div>
                <textarea onChange={onPostChange} ref={newPostElement}
                          value={props.newPostCar}/>
            </div>
            <div>
                <button onClick={addPost}>Add post </button>
            </div>
            <div>
                {postElements3}
            </div>
        </div>
    );
};

export default Car;
