let store = {
    _state: {
        dialogsData: [
            {id: 1, name: 'Dima'},
            {id: 2, name: 'Andre'},
            {id: 3, name: 'Sasha'},
        ],
        messagesData: [
            {id: 1, message: 'hi'},
            {id: 2, message: 'hi'},
            {id: 3, message: 'hi'},
            {id: 4, message: 'hi'},
        ],
        posts: [
            {id: 1, message: 'hi'},
            {id: 2, message: 'ho'}
        ],
        newPostText: ' re'
    },
    _callSubscriber () {
        console.log('state is change');
    },
    getState( ) {
        return this._state;
    },
    addPost () {
        let newPost = {
            id: 3,
            message: this._state.newPostText,
            /*likesCount:0*/
        };

        this._state.posts.push(newPost);
        this._state.newPostText = ''
        this._callSubscriber(this._state);
    },
    updateNewPostText (newText) {
        this._state.newPostText = newText;
        this._callSubscriber(this._state);
    },
    subscribe (observer) {
        this._callSubscriber = observer;
    },
}








export default store;
window.state = store;
