import React from 'react';
import {addPostCarCreator, updateNewPostTextCarCreator} from "../../redux/car-reducer";
import Car from "./Car";
import {connect} from "react-redux";

let mapStateToProps = (state) => {
    return{
        messagesCar: state.profileCar.messagesCar,
        newPostCar: state.profileCar.newPostCar
    }
}

let mapDispatchToProps = (dispatch) => {
    return{
        updateNewPostTextCarCreator: (newCar) => {
            let action = updateNewPostTextCarCreator(newCar);
            dispatch(action);
        },
        addPost: () => {
            dispatch(addPostCarCreator());

        }
    }
}

const CarContainer = connect (mapStateToProps, mapDispatchToProps)(Car);

export default CarContainer;
