import React from 'react';
import {Field, reduxForm} from "redux-form";
import {Input} from "../Common/FormsControls/FormsControls";
import {required} from "../../utils/validators/validators";
import {connect} from "react-redux";
import {login} from "../../redux/auth-reducer";

const LoginForm = (props) => {
    return (
            <form onSubmit={props.handleSubmit}>
                <div>
                    <Field  placeholder={"Email"} name={"email"}
                            validate={[required]}
                            component={Input}/>
                </div>
                <div>
                    <Field placeholder={"Password"} name={"password"}
                           validate={[required]}
                           component={Input}/>
                </div>
                <div>
                    <Field component={Input} name={"rememberMe"} type={"checkbox"}/> remember me
                </div>
                <div>
                    <button>login</button>
                </div>
            </form>
    );
};

const LoginReduxForm = reduxForm({
form: 'login' // уникальное строковое имя , что бі стейт различал разніе формі
})(LoginForm); // вокруг чего обворачиваем

const Login = (props) => {
    const onSubmit = (formData) => {//  в formData сидят методы из сабмита которые он получает из библиотеки редакс-форм мы их прокидываем дальше в нашу компоненту
    props.login(formData.email, form);
    }
    return (
        <div>
            <h1>login</h1>
            <LoginReduxForm onSubmit={onSubmit}/>
        </div>
    );
};

export default connect(null, {login}) (Login);
