import React from 'react';
import PostCar from "./PostCar/PostCar";
import {Field, reduxForm} from "redux-form";
import {maxLengthCreator, required} from "../../utils/validators/validators";
import {Textarea} from "../Common/FormsControls/FormsControls";
import { Redirect } from "react-router-dom"

const Car = (props) => {
    if (!props.isAuth) return <Redirect to={"/login"} /> ;


    let postElements3 =
        props.messagesCar.map(p => <PostCar message={p.message}/>);

    let newPostElement = React.createRef();

    let addPost = (values) => {
        props.addPost(values.newPostCar);
    }

    return (
        <div>
            <AddNewPostCarFormRedux onSubmit={addPost}/>
            <div>
                {postElements3}
            </div>
        </div>
    );
};
const maxLength50 = maxLengthCreator(50);

const addNewPost = (props) => {
    return (
        <form onSubmit={props.handleSubmit}>
            <div>
                <Field name="newPostCar" component={Textarea}
                       validate={[required, maxLength50]} />
            </div>
            <div>
                <button >Add post</button>
            </div>
        </form>
    )
}

let AddNewPostCarFormRedux = reduxForm({form: "CarAddNewPostForm"})(addNewPost);


export default Car;
