import React from 'react';
import ProfileContainer from "./ProfileContainer";
import {connect} from "react-redux";
import axios from "axios";
import {setUsersProfile} from "../../redux/profile-reducer";
import {withRouter} from "react-router-dom";
import {userAPI} from "../../api/api";


//df
class ProfileBossContainer extends  React.Component{
    componentDidMount() {
        let userId =this.props.match.params.userId; // метод позволяющий получить userId использ withRouter
        if(!userId){
            userId = 2;
        }
        userAPI.getProfile(userId).then(response => {
                this.props.setUsersProfile(response.data);
            });
    }

    render() {
        return (
            <div>
                <ProfileContainer {...this.props} profile={this.props.profile}/>
            </div>
        );
    };
}
let mapStateToProps = (state) => ({
 profile: state.profilePage.profile
});

let WithUrlDataContainerComponent = withRouter(ProfileBossContainer);

export default connect(mapStateToProps, {setUsersProfile}) (WithUrlDataContainerComponent);
